CREATE TABLE "Pelicula" (
	"idPelicula" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"tituloPelicula" VARCHAR(70) NOT NULL,
	"anioLanzamiento" INTEGER NOT NULL,
	PRIMARY KEY("idPelicula")
);




CREATE TABLE "Actor" (
	"idActor" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nombreActor" VARCHAR(50) NOT NULL,
	"fechaNacimiento" DATE NOT NULL,
	PRIMARY KEY("idActor")
);




CREATE TABLE "Elenco" (
	"idElenco" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"idPelicula" INTEGER NOT NULL,
	"idActor" INTEGER NOT NULL,
	PRIMARY KEY("idElenco")
);



ALTER TABLE "Pelicula"
ADD FOREIGN KEY("idPelicula") REFERENCES "Elenco"("idElenco")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "Elenco"
ADD FOREIGN KEY("idElenco") REFERENCES "Actor"("idActor")
ON UPDATE NO ACTION ON DELETE NO ACTION;