CREATE TABLE "Cliente" (
	"dniCliente" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"telefonoCliente" INTEGER NOT NULL,
	PRIMARY KEY("dniCliente")
);




CREATE TABLE "Pedido" (
	"idPedido" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"fechaPedido" DATE NOT NULL,
	"estadoPedido" VARCHAR(55) NOT NULL,
	PRIMARY KEY("idPedido")
);




CREATE TABLE "Menu" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nombrePlato" VARCHAR(80) NOT NULL,
	"descripcionPlato" TEXT NOT NULL,
	"precioPlato" DOUBLE PRECISION NOT NULL,
	PRIMARY KEY("id")
);



ALTER TABLE "Cliente"
ADD FOREIGN KEY("dniCliente") REFERENCES "Pedido"("idPedido")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "Menu"
ADD FOREIGN KEY("id") REFERENCES "Pedido"("idPedido")
ON UPDATE NO ACTION ON DELETE NO ACTION;